package dmitriy.blanutsa.coursework.controllers;import dmitriy.blanutsa.coursework.controllers.dto.AttorneyPowerRequest;import dmitriy.blanutsa.coursework.services.AttorneyPowerService;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PostMapping;import javax.validation.Valid;@Controllerpublic class AttorneyPowerController {    private final AttorneyPowerService service;    public AttorneyPowerController(AttorneyPowerService service) {        this.service = service;    }    @GetMapping("/attorneyPowers")    private String models(Model model) {        return service.getList(model);    }    @GetMapping("/addAttorneyPower")    private String addModel(Model model) {        service.setCreateAttorneyPowerModelObject(model);        return "addAttorneyPower";    }    @PostMapping("/addAttorneyPower")    private String addModel(@ModelAttribute("attorneyPower") @Valid AttorneyPowerRequest request, BindingResult result, Model model) {        return service.add(request, result, model);    }}