package dmitriy.blanutsa.coursework.controllers;import dmitriy.blanutsa.coursework.controllers.dto.AttorneyPowerRequest;import dmitriy.blanutsa.coursework.persistance.models.AttorneyPower;import dmitriy.blanutsa.coursework.services.AttorneyPowerService;import dmitriy.blanutsa.coursework.services.CarService;import dmitriy.blanutsa.coursework.services.UserService;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PostMapping;import javax.validation.Valid;@Controllerpublic class AttorneyPowerController {    private final AttorneyPowerService service;    private final UserService userService;    private final CarService carService;    public AttorneyPowerController(AttorneyPowerService service, UserService userService, CarService carService) {        this.service = service;        this.userService = userService;        this.carService = carService;    }    @GetMapping("/attorneyPowers")    private String models(Model model) {        return service.getList(model);    }    @GetMapping("/addAttorneyPower")    private String addModel(Model model) {        model.addAttribute("attorneyPower", new AttorneyPower());        model.addAttribute("persons", userService.getListWithoutCurrent());        model.addAttribute("cars", carService.getCurrentCars());        return "addAttorneyPower";    }    @PostMapping("/addAttorneyPower")    private String addModel(@ModelAttribute("attorneyPower") @Valid AttorneyPowerRequest request, BindingResult result, Model model) {        return service.add(request, result, model);    }}