package dmitriy.blanutsa.coursework.controllers;import dmitriy.blanutsa.coursework.controllers.dto.CarRequest;import dmitriy.blanutsa.coursework.controllers.dto.ModelRequest;import dmitriy.blanutsa.coursework.persistance.models.Car;import dmitriy.blanutsa.coursework.persistance.models.enums.CarType;import dmitriy.blanutsa.coursework.persistance.repositories.ManufacturerRepository;import dmitriy.blanutsa.coursework.persistance.repositories.ModelRepository;import dmitriy.blanutsa.coursework.services.CarService;import dmitriy.blanutsa.coursework.services.ModelService;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PostMapping;import javax.validation.Valid;@Controllerpublic class CarController {    private final CarService service;    private final ModelRepository modelRepository;    public CarController(CarService service, ModelRepository modelRepository) {        this.service = service;        this.modelRepository = modelRepository;    }    @GetMapping("/cars")    private String models(Model model) {        return service.getList(model);    }    @GetMapping("/addCar")    private String addModel(Model model) {        model.addAttribute("car", new Car());        model.addAttribute("models", modelRepository.findAll());        return "addCar";    }    @PostMapping("/addCar")    private String addModel(@ModelAttribute("car") @Valid CarRequest request, BindingResult result, Model model) {        return service.add(request, result, model);    }}