package dmitriy.blanutsa.coursework.controllers;import dmitriy.blanutsa.coursework.controllers.dto.ManufacturerRequest;import dmitriy.blanutsa.coursework.persistance.models.Manufacturer;import dmitriy.blanutsa.coursework.services.ManufacturerService;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PostMapping;import javax.validation.Valid;@Controllerpublic class ManufacturerController {    private final ManufacturerService service;    public ManufacturerController(ManufacturerService service) {        this.service = service;    }    @GetMapping("/manufactures")    private String manufactures(Model model) {        return service.getList(model);    }    @GetMapping("/addManufacturer")    private String addManufacturer(Model model) {        model.addAttribute("manufacturer", new Manufacturer());        return "addManufacturer";    }    @PostMapping("/addManufacturer")    private String addManufacturer(@ModelAttribute("manufacturer") @Valid ManufacturerRequest request, BindingResult result) {        return service.add(request, result);    }}