package dmitriy.blanutsa.coursework.controllers;import dmitriy.blanutsa.coursework.controllers.dto.ModelRequest;import dmitriy.blanutsa.coursework.persistance.models.Manufacturer;import dmitriy.blanutsa.coursework.persistance.models.enums.CarType;import dmitriy.blanutsa.coursework.persistance.repositories.ManufacturerRepository;import dmitriy.blanutsa.coursework.services.ModelService;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PostMapping;import javax.validation.Valid;@Controllerpublic class ModelController {    private final ModelService service;    private final ManufacturerRepository manufacturerRepository;    public ModelController(ModelService service, ManufacturerRepository manufacturerRepository) {        this.service = service;        this.manufacturerRepository = manufacturerRepository;    }    @GetMapping("/models")    private String models(Model model) {        return service.getList(model);    }    @GetMapping("/addModel")    private String addModel(Model model) {        model.addAttribute("model", new dmitriy.blanutsa.coursework.persistance.models.Model());        model.addAttribute("types", CarType.values());        model.addAttribute("manufactures", manufacturerRepository.findAll());        return "addModel";    }    @PostMapping("/addModel")    private String addModel(@ModelAttribute("model") @Valid ModelRequest request, BindingResult result, Model model) {        return service.add(request, result, model);    }}