package dmitriy.blanutsa.coursework.controllers.dto;import javax.validation.constraints.NotBlank;import java.io.Serializable;public class ManufacturerRequest implements Serializable {    private static final long serialVersionUID = 1850007121896179818L;    @NotBlank(message = "Name is required")    private String name;    private String description;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }}