package dmitriy.blanutsa.coursework.controllers.dto;import dmitriy.blanutsa.coursework.persistance.models.Person;import javax.validation.constraints.NotBlank;import java.io.Serializable;public class ProfileDTO implements Serializable {    private static final long serialVersionUID = 6355289843804590128L;    private String login;    @NotBlank(message = "First name is required!!!")    private String firstName;    @NotBlank(message = "Last name is required!!!")    private String lastName;    public String getLogin() {        return login;    }    public void setLogin(String login) {        this.login = login;    }    public String getFirstName() {        return firstName;    }    public void setFirstName(String firstName) {        this.firstName = firstName;    }    public String getLastName() {        return lastName;    }    public void setLastName(String lastName) {        this.lastName = lastName;    }    public static ProfileDTO fromPerson(Person person) {        ProfileDTO profileDTO = new ProfileDTO();        profileDTO.setLogin(person.getLogin());        profileDTO.setLastName(person.getLastName());        profileDTO.setFirstName(person.getFirstName());        return  profileDTO;    }}