package dmitriy.blanutsa.coursework.controllers.dto;import dmitriy.blanutsa.coursework.persistance.models.Person;import javax.validation.constraints.NotBlank;import java.io.Serializable;public class RegisterRequest implements Serializable {    private static final long serialVersionUID = -3819705851077824742L;    @NotBlank(message = "Login is required!!!")    private String login;    @NotBlank(message = "Password is required!!!")    private String password;    @NotBlank(message = "First name is required!!!")    private String firstName;    @NotBlank(message = "Last name is required!!!")    private String lastName;    public String getLogin() {        return login;    }    public void setLogin(String login) {        this.login = login;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getFirstName() {        return firstName;    }    public void setFirstName(String firstName) {        this.firstName = firstName;    }    public String getLastName() {        return lastName;    }    public void setLastName(String lastName) {        this.lastName = lastName;    }    public Person toPerson () {        Person person = new Person();        person.setLastName(lastName);        person.setLogin(login);        person.setPassword(password);        person.setFirstName(firstName);        return person;    }}