package dmitriy.blanutsa.coursework.controllers.dto;import javax.validation.constraints.NotBlank;import java.io.Serializable;public class ResetPasswordRequest implements Serializable {    private static final long serialVersionUID = -4213037206076662043L;    @NotBlank(message = "Old password is required!!!")    private String currentPassword;    @NotBlank(message = "New password is required!!!")    private String newPassword;    public String getCurrentPassword() {        return currentPassword;    }    public void setCurrentPassword(String currentPassword) {        this.currentPassword = currentPassword;    }    public String getNewPassword() {        return newPassword;    }    public void setNewPassword(String newPassword) {        this.newPassword = newPassword;    }}