package dmitriy.blanutsa.coursework.controllers.dto;import java.io.Serializable;public class SignInRequest implements Serializable {    private static final long serialVersionUID = 5390947863139708553L;    private String login;    private String password;    public String getLogin() {        return login;    }    public void setLogin(String login) {        this.login = login;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }}