package dmitriy.blanutsa.coursework.persistance.models;import javax.persistence.*;import java.util.Date;@Entity@NamedQueries({     @NamedQuery(name = AttorneyPower.GET_ALL_MY_AT, query =        "SELECT ap FROM AttorneyPower ap " +        "WHERE ap.car.ownerId=:userId"     ),     @NamedQuery(name = AttorneyPower.GET_ALL_ANOTHER_AT, query =        "SELECT ap FROM AttorneyPower ap " +        "WHERE ap.personId=:userId"     )})public class AttorneyPower extends BaseEntity {    public static final String GET_ALL_MY_AT = "AttorneyPower.GET_ALL_MY_AT";    public static final String GET_ALL_ANOTHER_AT = "AttorneyPower.GET_ALL_ANOTHER_AT";    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "person_id", nullable = false)    private Person person;    @Column(name = "person_id", nullable = false, insertable = false, updatable = false)    private Integer personId;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "car_id", nullable = false)    private Car car;    @Column(name = "car_id", nullable = false, insertable = false, updatable = false)    private Integer carId;    @Column(name = "date_from", nullable = false)    @Temporal(value = TemporalType.DATE)    private Date dateFrom;    @Column(name = "date_to", nullable = false)    @Temporal(value = TemporalType.DATE)    private Date dateTo;    public Person getPerson() {        return person;    }    public void setPerson(Person person) {        this.person = person;    }    public Integer getPersonId() {        return personId;    }    public void setPersonId(Integer personId) {        this.personId = personId;    }    public Car getCar() {        return car;    }    public void setCar(Car car) {        this.car = car;    }    public Integer getCarId() {        return carId;    }    public void setCarId(Integer carId) {        this.carId = carId;    }    public Date getDateFrom() {        return dateFrom;    }    public void setDateFrom(Date dateFrom) {        this.dateFrom = dateFrom;    }    public Date getDateTo() {        return dateTo;    }    public void setDateTo(Date dateTo) {        this.dateTo = dateTo;    }}