package dmitriy.blanutsa.coursework.persistance.models;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.MappedSuperclass;import java.sql.Date;@MappedSuperclasspublic abstract class BaseEntity {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Integer id;    @CreationTimestamp    private Date createAt;    @UpdateTimestamp    private Date updatedAt;    public Integer getId() {        return id;    }    public void setId(Integer id) {        this.id = id;    }    public Date getCreateAt() {        return createAt;    }    public void setCreateAt(Date createAt) {        this.createAt = createAt;    }    public Date getUpdatedAt() {        return updatedAt;    }    public void setUpdatedAt(Date updatedAt) {        this.updatedAt = updatedAt;    }}