package dmitriy.blanutsa.coursework.persistance.models;import javax.persistence.*;import java.util.List;@Entitypublic class Manufacturer extends BaseEntity {    @Column(name = "name", nullable = false)    private String name;    @Column(name = "description")    private String description;    @OneToMany(fetch = FetchType.LAZY, cascade = CascadeType.ALL, mappedBy = "manufacturer")    private List<Model> models;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }}