package dmitriy.blanutsa.coursework.persistance.models;import dmitriy.blanutsa.coursework.persistance.models.enums.CarType;import javax.persistence.*;@Entitypublic class Model extends BaseEntity {    @Column(name = "name", nullable = false)    private String name;    @Column(name = "description")    private String description;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "manufacturer_id", nullable = false)    private Manufacturer manufacturer;    @Column(name = "manufacturer_id", nullable = false, insertable = false, updatable = false)    private Integer manufacturerId;    @Column(name = "type", nullable = false)    @Enumerated(value = EnumType.STRING)    private CarType type;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public Manufacturer getManufacturer() {        return manufacturer;    }    public void setManufacturer(Manufacturer manufacturer) {        this.manufacturer = manufacturer;    }    public Integer getManufacturerId() {        return manufacturerId;    }    public void setManufacturerId(Integer manufacturerId) {        this.manufacturerId = manufacturerId;    }    public CarType getType() {        return type;    }    public void setType(CarType type) {        this.type = type;    }}