package dmitriy.blanutsa.coursework.persistance.models;import dmitriy.blanutsa.coursework.persistance.models.enums.UserRoles;import javax.persistence.*;import java.util.List;import java.util.Set;@Entity@Table(uniqueConstraints = {        @UniqueConstraint(columnNames = {"login"})})@NamedQueries({    @NamedQuery(name = Person.ALL_WITHOUT_ONE, query =        "SELECT p " +        "FROM Person p " +        "WHERE p.login != :login"    )})public class Person extends BaseEntity {    public static final String ALL_WITHOUT_ONE = "Person.ALL_WITHOUT_ONE";    @Column(name = "first_name", nullable = false)    private String firstName;    @Column(name = "last_name", nullable = false)    private String lastName;    @Column(name = "password", nullable = false)    private String password;    @Column(name = "login", nullable = false)    private String login;    @ElementCollection(targetClass = UserRoles.class)    @CollectionTable(name = "roles", joinColumns = @JoinColumn(name = "user_id"))    @Column(name = "role", nullable = false)    @Enumerated(EnumType.STRING)    private Set<UserRoles> roles;    @OneToMany(fetch = FetchType.LAZY, cascade = CascadeType.ALL, mappedBy = "owner")    private List<Car> cars;    @OneToMany(fetch = FetchType.LAZY, cascade = CascadeType.ALL, mappedBy = "person")    private List<AttorneyPower> attorneyPowers;    public List<Car> getCars() {        return cars;    }    public void setCars(List<Car> cars) {        this.cars = cars;    }    public List<AttorneyPower> getAttorneyPowers() {        return attorneyPowers;    }    public void setAttorneyPowers(List<AttorneyPower> attorneyPowers) {        this.attorneyPowers = attorneyPowers;    }    public String getFirstName() {        return firstName;    }    public void setFirstName(String firstName) {        this.firstName = firstName;    }    public String getLastName() {        return lastName;    }    public void setLastName(String lastName) {        this.lastName = lastName;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getLogin() {        return login;    }    public void setLogin(String login) {        this.login = login;    }    public Set<UserRoles> getRoles() {        return roles;    }    public void setRoles(Set<UserRoles> roles) {        this.roles = roles;    }}