package dmitriy.blanutsa.coursework.persistance.repositories;import dmitriy.blanutsa.coursework.persistance.models.AttorneyPower;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface AttorneyPowerRepository extends JpaRepository<AttorneyPower, Integer> {    @Query(name = AttorneyPower.GET_ALL_MY_AT)    List<AttorneyPower> findListByPersonId(@Param("userId") Integer personId);    @Query(name = AttorneyPower.GET_ALL_ANOTHER_AT)    List<AttorneyPower> findAnotherListByPersonId(@Param("userId") Integer personId);}