package dmitriy.blanutsa.coursework.persistance.repositories;import dmitriy.blanutsa.coursework.persistance.models.Person;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface PersonRepository extends JpaRepository<Person, Integer> {    @Query(name = Person.ALL_WITHOUT_ONE)    List<Person> getAllWithoutOne(@Param("login") String login);    Person findByLogin(String login);}