package dmitriy.blanutsa.coursework.security;import dmitriy.blanutsa.coursework.persistance.models.Person;import dmitriy.blanutsa.coursework.persistance.repositories.PersonRepository;import org.springframework.security.core.userdetails.User;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Component;import javax.transaction.Transactional;import java.util.HashSet;@Component("customUserDetailsService")public class UserDetailsServiceImpl implements UserDetailsService {    private PersonRepository personRepository;    public UserDetailsServiceImpl(PersonRepository personRepository) {        this.personRepository = personRepository;    }    @Override    @Transactional    public UserDetails loadUserByUsername(String username) throws UsernameNotFoundException {        Person person = personRepository.findByLogin(username);        return new User(person.getLogin(), person.getPassword(), new HashSet<>());    }}